import React, { Component } from 'react'
import { connect } from 'react-redux'
import MaterialTable from 'material-table';
import {getUser , addUser ,updateUser,deleteUser} from '../action/user.action'

const     columns =  [
    { title: 'Name', field: 'name' },
    { title: 'Surname', field: 'surName' },
    { title: 'Birth Year', field: 'birthDay', type: 'date' },
    {
      title: 'Birth Place',
      field: 'birthPlace',
    },
  ]
export class UserTable extends Component {
    state = {
    
          
    }
    componentDidMount =() => {
        this.props.getUser()
    }

    
    render() {
        const {userList,isLoading , addUser ,updateUser,deleteUser} = this.props
        console.log("userList" ,userList)
        return (
            <div className ='table-container'>
                <MaterialTable 
                className='table-content'
                 title="Editable Example"
                 data={userList}
                 columns={ columns}
                 isLoading = {isLoading}

                  editable={{
                    onRowAdd: newData =>
                    new Promise(resolve => {
                      setTimeout(() => {
                        resolve();
                        addUser(newData)
                      }, 600);
                    }),
                    onRowUpdate: (newData, oldData) =>
                    new Promise(resolve => {
                      setTimeout(() => {
                        resolve();
                        updateUser(oldData._id , newData)
                      }, 600);
                    }),
                  onRowDelete: oldData =>
                    new Promise(resolve => {
                      setTimeout(() => {
                        resolve();
                        deleteUser(oldData._id)
                      }, 600);
                    }),
                          }}
                 />
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    userList : state.userList,
    isLoading: state.loading
})
 
export default connect(mapStateToProps, {getUser ,addUser ,updateUser,deleteUser})(UserTable)
