import React, { Component } from 'react'
import { connect } from 'react-redux'
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import {
    getImages,addImage,deleteImage
  } from "../action/image.action";
export class Galerie extends Component {
    componentDidMount = ()=> {
        const {userId} = this.props.match.params
        console.log(userId)
        this.props.getImages(userId)
    }
    render() {
        const {imageList , loading} = this.props
        console.log('imageList', imageList)
         if (loading) {
             return <span>spinner</span>
         }
        return (
            <div>
            {imageList.map(image => {
                return (
                    <Card  style= {{maxWidth: 345}} >
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image="/static/images/cards/contemplative-reptile.jpg"
          title="Contemplative Reptile"
        />
 
      </CardActionArea>
      <CardActions>
      <Typography variant="body2" color="textSecondary" component="p">
       titre
          </Typography>
        <Button size="small" color="primary">
          Share
        </Button>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card> 
                )
            })}
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    imageList : state.imageList,
    loading : state.loading
})

const mapDispatchToProps = {
    
}

export default connect(mapStateToProps, {getImages})(Galerie)
