import React, { Component } from "react";
import { connect } from "react-redux";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import { Input } from "@material-ui/core";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

import { getImages, addImage, deleteImage } from "../action/image.action";
export class Galerie extends Component {
  state = {
    search: ""
  };
  componentDidMount = () => {
    const { userId } = this.props.match.params;
    console.log(userId);
    this.props.getImages(userId);
  };
  render() {
    const { userId } = this.props.match.params;
    const { imageList, loading, deleteImage } = this.props;
    const { search } = this.state;
    console.log("imageList", imageList);
    if (loading) {
      return <span>spinner</span>;
    }
    return (
      <div className="galeries">
        <div className="galeries-header">
          <Input
            placeholder="search"
            onChange={(e) => this.setState({ search: e.target.value })}
          />
          <Button size="small" color="primary">
            add
          </Button>
        </div>
        {imageList &&
          imageList
            .filter((image) => image.name.startsWith(search))
            .map((image) => {
              return (
                <Card style={{ maxWidth: 345 }} className="galeries-card">
                  <CardActionArea>
                    <CardMedia
                      style={{ height: 140 }}
                      image={image.link}
                      title="Contemplative Reptile"
                    />
                  </CardActionArea>
                  <CardActions className="galerie-action-zone">
                    <Typography
                      variant="body2"
                      color="textSecondary"
                      component="p">
                      {image.name}
                    </Typography>
                    <div>
                      <Button
                        size="small"
                        color="primary"
                        onClick={() => deleteImage(userId, image._id)}>
                        delete
                      </Button>
                      <Button size="small" color="primary">
                        zoom
                      </Button>
                    </div>
                  </CardActions>
                </Card>
              );
            })}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  imageList: state.imageList,
  loading: state.loading
});

const mapDispatchToProps = {};

export default connect(
  mapStateToProps,
  { getImages, deleteImage }
)(Galerie);
